// src/components/Sidebar.js
import React from 'react';
import { Link } from 'react-router-dom';
import {
  Drawer,
  List,
  ListItem,
  ListItemIcon,
  ListItemText
} from '@mui/material';

// Import icons
import DashboardIcon from '@mui/icons-material/Dashboard';
import SegmentIcon from '@mui/icons-material/Category';
import JourneyIcon from '@mui/icons-material/Timeline';
import PersonalizationIcon from '@mui/icons-material/Tune';
import ChurnIcon from '@mui/icons-material/Warning';
import ReferralIcon from '@mui/icons-material/Share';
import FeatureIcon from '@mui/icons-material/Extension';

const Sidebar = ({ open, handleDrawerClose }) => {
  const menuItems = [
    { 
      text: 'Dashboard', 
      icon: <DashboardIcon />, 
      path: '/dashboard' 
    },
    { 
      text: 'User Segments', 
      icon: <SegmentIcon />, 
      path: '/segments' 
    },
    { 
      text: 'User Journey', 
      icon: <JourneyIcon />, 
      path: '/user-journey' 
    },
    { 
      text: 'Personalization', 
      icon: <PersonalizationIcon />, 
      path: '/personalization' 
    },
    { 
      text: 'Churn Prediction', 
      icon: <ChurnIcon />, 
      path: '/churn-prediction' 
    },
    { 
      text: 'Referral Insights', 
      icon: <ReferralIcon />, 
      path: '/referral-insights' 
    },
    { 
      text: 'Feature Usage', 
      icon: <FeatureIcon />, 
      path: '/feature-usage' 
    }
  ];

  return (
    <Drawer
      variant="persistent"
      anchor="left"
      open={open}
      sx={{
        width: 240,
        flexShrink: 0,
        '& .MuiDrawer-paper': {
          width: 240,
          boxSizing: 'border-box',
        },
      }}
    >
      <List>
        {menuItems.map((item) => (
          <ListItem 
            button 
            key={item.text} 
            component={Link} 
            to={item.path}
          >
            <ListItemIcon>{item.icon}</ListItemIcon>
            <ListItemText primary={item.text} />
          </ListItem>
        ))}
      </List>
    </Drawer>
  );
};

export default Sidebar;
